<script>
//这里可以导入其他文件（比如：组件，工具js，第三方插件js，json文件，图片文件等等）
//例如：import 《组件名称》 from '《组件路径》';
import {taskInfo,
    initTask,
    preSignUrl,
    merge,
    getZoneTree,
    getRecommendTags,
    uploadVieo,
    videoEntity,
    addLike,
    isLikeAPI,
    getPlayUrl,
    sendReplyAPI,
    getReplyMain
  } from "../lib/api";
export default {
  name: "video",
  //import引入的组件需要注入到对象中才能使用
  components: {},
  data() {
    //这里存放数据
    return {
      videoId: 0,
      video: {},
      member: {},
      isLike: false,
      options: {
        screenshot: true,
        video: {
          url: "",
          pic: ""
        },
        autoplay: false,
        contextmenu: [
          {
            text: "GitHub",
            link: "https://github.com/MoePlayer/vue-dplayer"
          }
        ],
        subtitle: {
          url: "",
          type: "webvtt",
          fontSize: "25px",
          bottom: "10%",
          color: "#b7daff"
        },
        danmaku: {
          id: "",
          api: "http://localhost:8090/apix/danmaku/",
          token: "tokendemo",
          maximum: 1000,
          user: "",
          bottom: "15%",
          unlimited: true,
          speedRate: 0.5
        }
      },
      player: null,
      reply_text: "",
      replies: [],
    };
  },
  //监听属性 类似于data概念
  computed: {},
  //监控data中的数据变化
  watch: {},
  //方法集合
  methods: {
    play() {
      console.log("play callback");
    },
    addLike() {
      addLike(this.videoId, !this.isLike, this.video.mid).then(res => {
        console.log(res);
        if (res.code === 200) {
          if (this.isLike) {
            this.video.like = this.video.like - 1;
          } else {
            this.video.like = this.video.like + 1;
          }
          this.isLike = !this.isLike;
        }
      });
    },
    sendReply(){
      if(this.reply_text != ""){
        console.log(this.reply_text);
        sendReplyAPI(this.videoId,this.reply_text,0,0).then(res=>{
          console.log(res);
          this.reply_text="";
        })
      }
    }
  },
  //生命周期 - 创建完成（可以访问当前this实例）
  created() {
    this.videoId = this.$route.params.id;
    this.options.danmaku.id = this.videoId
    videoEntity(this.videoId).then(res => {
      console.log(res);
      this.video = res.data;
      this.member = res.data.memberInfo;
      //this.options.video.url = "http://127.0.0.1:8090/apix/video/play?videoId=396";
      getPlayUrl(res.data.playUrl).then(res => {
        console.log(res);
        this.options.danmaku.user = this.member.nickName
        this.player.switchVideo({
          //url: "http://127.0.0.1:8090/apix/video/play?videoId=396"
          url: res.data
        });
      });
      isLikeAPI(0, this.videoId).then(res => {
        this.isLike = res.data;
      });
      getReplyMain(this.videoId,5,1,null).then(res=>{
        console.log(res)
        this.replies = res.data.replies
      })
    });
  },
  //生命周期 - 挂载完成（可以访问DOM元素）
  mounted() {
    this.player = this.$refs.player.dp;
  },
  beforeCreate() {}, //生命周期 - 创建之前
  beforeMount() {}, //生命周期 - 挂载之前
  beforeUpdate() {}, //生命周期 - 更新之前
  updated() {}, //生命周期 - 更新之后
  beforeDestroy() {}, //生命周期 - 销毁之前
  destroyed() {}, //生命周期 - 销毁完成
  activated() {} //如果页面有keep-alive缓存功能，这个函数会触发
};
</script>
